        [HttpGet("{id}/members")]
        public async Task<ActionResult> GetMembers([FromRoute] string id)
        {
            var membersList = new List<MemberDto>();
            try
            {
                using (_client)
                {
                    HttpResponseMessage response = await _client.GetAsync($"organizations/{id}/members");
                    if (response.IsSuccessStatusCode)
                    {
                        var objResponse = response.Content.ReadAsStringAsync().Result;
                        membersList = JsonConvert.DeserializeObject<List<MemberDto>>(objResponse);
                        return Ok(membersList);
                    }

                    return NotFound();
                };
            }
            catch (Exception)
            {
                throw;
            }
        }

        [HttpGet("{id}/info")]
        public async Task<ActionResult> GetInfo([FromRoute] string id)
        {
            try
            {
                using (_client)
                {
                    HttpResponseMessage response = await _client.GetAsync($"organizations/{id}");
                    if (response.IsSuccessStatusCode)
                    {
                        var objResponse = response.Content.ReadAsStringAsync().Result;
                        var organization = JsonConvert.DeserializeObject<OrganizationDto>(objResponse);
                        return Ok(organization);
                    }

                    return NotFound();
                };
            }
            catch (Exception)
            {
                throw;
            }
        }
    }
